function SaeMemcacheAdd($a,$b){if(strlen($a)>234){$a=substr($a,-234);}$c=memcache_init();if($c==false){return false;}else{return memcache_add($c,$a,$b);}}function SaeMemcacheWrite($a,$b){if(strlen($a)>234){$a=substr($a,-234);}$c=memcache_init();if($c==false){return false;}else{return memcache_set($c,$a,$b);}}function SaeMemcacheReplace($a,$b){if(strlen($a)>234){$a=substr($a,-234);}$c=memcache_init();if($c==false){return false;}else{return memcache_replace($c,$a,$b);}}function SaeMemcacheIncrement($a,$b){if(strlen($a)>234){$a=substr($a,-234);}$c=memcache_init();if($c==false){return false;}else{return memcache_increment($c,$a,$b);}}function SaeMemcacheDecrement($a,$b){if(strlen($a)>234){$a=substr($a,-234);}$c=memcache_init();if($c==false){return false;}else{return memcache_decrement($c,$a,$b);}}function SaeMemcacheRead($a){if(strlen($a)>234){$a=substr($a,-234);}$c=memcache_init();if($c==false){return false;}else{return memcache_get($c,$a);}}function SaeMemcacheDelete($a){if(strlen($a)>234){$a=substr($a,-234);}$c=memcache_init();if($c==false){return false;}else{return memcache_delete($c,$a);}}function SaeMemcacheStatus(){$c=memcache_init();if($c==false){return false;}else{return $c->getStats();}}function SaeMemcachePercent(){$c=memcache_init();if($c==false){return false;}else{$d=$c->getStats();if($d==false){return false;}return round($d["bytes"]/$d["limit_maxbytes"]*100,2)."%";}}function SaeMemcacheFlush(){$c=memcache_init();if($c==false){return false;}else{return $c->flush();}}?>