$a=SAE_MYSQL_HOST_M;$b=SAE_MYSQL_PORT;$c=SAE_MYSQL_USER;$d=SAE_MYSQL_PASS;$e=SAE_MYSQL_DB;function SaeMySQLConnect(){global $a;global $b;global $c;global $d;global $e;$f=mysql_connect($a.':'.$b,$c,$d);if(!$f){return false;}if(!mysql_select_db($e,$f)){return false;}return $f;}function SaeMySQLTryUpdate($g){$h=mysql_query($g);if(!$h){return false;}return $h;}function SaeMySQLMustUpdate($g){$h=mysql_query($g);if(!$h){return false;}if(mysql_affected_rows()===0){return false;}return $h;}function SaeMySQLTryDelete($g){$h=mysql_query($g);if(!$h){return false;}return $h;}function SaeMySQLMustDelete($g){$h=mysql_query($g);if(!$h){return false;}if(mysql_affected_rows()===0){return false;}return $h;}function SaeMySQLInsert($g){$h=mysql_query($g);if(!$h){return false;}if(mysql_insert_id()===0){return false;}return mysql_insert_id();}function SaeMySQLSelectArray($g){$h=mysql_query($g);if(!$h){return false;}$i=array();$j=mysql_fetch_array($h);while($j){$i[]=$j;$j=mysql_fetch_array($h);}return $i;}function SaeMySQLSelectDefaultArray($g){$h=mysql_query($g);if(!$h){return false;}$i=array();$j=mysql_fetch_row($h);while($j){$i[]=$j;$j=mysql_fetch_row($h);}return $i;}function SaeMySQLSelectAssociativeArray($g){$h=mysql_query($g);if(!$h){return false;}$i=array();$j=mysql_fetch_assoc($h);while($j){$i[]=$j;$j=mysql_fetch_assoc($h);}return $i;}function SaeMySQLSelectRow($g){$h=mysql_query($g);if(!$h){return false;}$j=mysql_fetch_array($h);if(!$j){return false;}return $j;}function SaeMySQLSelectDefaultRow($g){$h=mysql_query($g);if(!$h){return false;}$j=mysql_fetch_row($h);if(!$j){return false;}return $j;}function SaeMySQLSelectAssociativeRow($g){$h=mysql_query($g);if(!$h){return false;}$j=mysql_fetch_assoc($h);if(!$j){return false;}return $j;}function SaeMySQLDisconnect(){mysql_close();}function SaeMySQLFetchAllArray($h){$i=array();if($h===false){return $i;}$j=mysql_fetch_array($h);while($j){$i[]=$j;$j=mysql_fetch_array($h);}return $i;}function SaeMySQLFetchDefaultArray($h){$i=array();if($h===false){return $i;}$j=mysql_fetch_row($h);while($j){$i[]=$j;$j=mysql_fetch_row($h);}return $i;}function SaeMySQLFetchAssociativeArray($h){$i=array();if($h===false){return $i;}$j=mysql_fetch_assoc($h);while($j){$i[]=$j;$j=mysql_fetch_assoc($h);}return $i;}function SaeMySQLVersion(){return mysql_get_server_info();}function SaeMySQLProcesses(){return SaeMySQLFetchAssociativeArray(mysql_list_processes());}?>