define('Sae_Storage_Domain_Name','MoonLord');function SaeStorageWrite($a,$b){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$e=-1;$f=array();$g=false;$h=$c->write($d,$a,$b,$e,$f,$g);return $h;}function SaeStorageUpload($a,$i){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$e=-1;$f=array();$g=false;$h=$c->upload($d,$a,$i,$e,$f,$g);return $h;}function SaeStorageRead($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->read($d,$a);return $h;}function SaeStorageAppend($a,$j){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->read($d,$a);if($h===false){return false;}$b=$h.$j;$e=-1;$f=array();$g=false;$h=$c->write($d,$a,$b,$e,$f,$g);return $h;}function SaeStorageDelete($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->delete($d,$a);return $h;}function SaeStorageExist($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->fileExists($d,$a);return $h;}function SaeStorageGetFileAttr($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getAttr($d,$a);return $h;}function SaeStorageSetFileAttr($a,$k){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->setFileAttr($d,$a,$k);return $h;}function SaeStorageGetDomainAttr(){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getDomainAttr($d);return $h;}function SaeStorageSetDomainAttr($k){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->setDomainAttr($d,$k);return $h;}function SaeStorageCDN($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getCDNUrl($d,$a);return $h;}function SaeStorageURL($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getUrl($d,$a);return $h;}function SaeStorageCapacity(){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getDomainCapacity($d);return $h;}function SaeStorageNum(){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getFilesNum($d);if($h===false){$h=0;}return $h;}function SaeStorageList($l){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=array();$m=100;$n=0;while($o=$c->getList($d,$l,$m,$n)){foreach($o as $p){$h[]=$p;$q++;}}return $h;}function SaeStorageListByPath($r){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=array();$m=1000;$n=0;$s=true;while($o=$c->getListByPath($d,$r,$m,$n,$s)){foreach($o as $p){$h[]=$p;$q++;}}return $h;}function SaeStorageListByNoFoldPath($r){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=array();$m=1000;$n=0;$s=false;while($o=$c->getListByPath($d,$r,$m,$n,$s)){foreach($o as $p){$h[]=$p;$q++;}}return $h;}function SaeStorageDomains(){$c=new SaeStorage();$h=$c->listDomains();if($h===false){$h=array();}return $h;}function SaeStorageErrorMessage(){$c=new SaeStorage();$h=$c->errmsg();return $h;}function SaeStorageErrorNumber(){$c=new SaeStorage();$h=$c->errno();return $h;}?>