define('Sae_Storage_Domain_Name','MoonLord');function SaeStorageWrite($a,$b){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$e=-1;$f=array('encoding'=>'gzip');$g=true;$h=$c->write($d,$a,$b,$e,$f,$g);return $h;}function SaeStorageUpload($a,$i){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$e=-1;$f=array('encoding'=>'gzip');$g=true;$h=$c->upload($d,$a,$i,$e,$f,$g);return $h;}function SaeStorageRead($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->read($d,$a);return $h;}function SaeStorageDelete($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->delete($d,$a);return $h;}function SaeStorageExist($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->fileExists($d,$a);return $h;}function SaeStorageGetFileAttr($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getAttr($d,$a);return $h;}function SaeStorageSetFileAttr($a,$j){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->setFileAttr($d,$a,$j);return $h;}function SaeStorageGetDomainAttr(){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getDomainAttr($d);return $h;}function SaeStorageSetDomainAttr($j){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->setDomainAttr($d,$j);return $h;}function SaeStorageCDN($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getCDNUrl($d,$a);return $h;}function SaeStorageURL($a){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getUrl($d,$a);return $h;}function SaeStorageCapacity(){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getDomainCapacity($d);return $h;}function SaeStorageNum(){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=$c->getFilesNum($d);if($h===false){$h=0;}return $h;}function SaeStorageList($k){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=array();$l=100;$m=0;while($n=$c->getList($d,$k,$l,$m)){foreach($n as $o){$h[]=$o;$p++;}}return $h;}function SaeStorageListByPath($q){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=array();$l=1000;$m=0;$r=true;while($n=$c->getListByPath($d,$q,$l,$m,$r)){foreach($n as $o){$h[]=$o;$p++;}}return $h;}function SaeStorageListByNoFoldPath($q){$c=new SaeStorage();$d=Sae_Storage_Domain_Name;$h=array();$l=1000;$m=0;$r=false;while($n=$c->getListByPath($d,$q,$l,$m,$r)){foreach($n as $o){$h[]=$o;$p++;}}return $h;}function SaeStorageDomains(){$c=new SaeStorage();$h=$c->listDomains();if($h===false){$h=array();}return $h;}function SaeStorageErrorMessage(){$c=new SaeStorage();$h=$c->errmsg();return $h;}function SaeStorageErrorNumber(){$c=new SaeStorage();$h=$c->errno();return $h;}?>